```typescript
class StringHandler {
    private inputString: string;

    constructor(inputString: string) {
        this.inputString = inputString;
    }

    // Convert all characters to uppercase
    public convertToUpperCase(): string {
        return this.inputString.toUpperCase();
    }

    // Reverse the string
    public reverseString(): string {
        return this.inputString.split('').reverse().join('');
    }

    // Get the length of the string
    public getStringLength(): number {
        return this.inputString.length;
    }

    // Split the string into an array by a delimiter
    public splitString(delimiter: string): string[] {
        return this.inputString.split(delimiter);
    }

    // Check if the string contains a specific substring
    public containsSubstring(substring: string): boolean {
        return this.inputString.includes(substring);
    }
}

// Example usage
const myStringHandler = new StringHandler('Hello, world!');
console.log(myStringHandler.convertToUpperCase()); // Output: "HELLO, WORLD!"
console.log(myStringHandler.reverseString()); // Output: "!dlrow ,olleH"
console.log(myStringHandler.getStringLength()); // Output: 13
console.log(myStringHandler.splitString(', ')); // Output: ["Hello", "world!"]
console.log(myStringHandler.containsSubstring('world')); // Output: true
```

