```csharp
using System;

class TicketFriendCounter
{
    public static int CountFriendsWithSimilarTickets(int N, int M, int K, int[] tickets)
    {
        int johnTicket = tickets[N]; // John's ticket
        int count = 0;

        for (int i = 0; i < N; i++)
        {
            int friendTicket = tickets[i];
            int difference = GetBitDifference(johnTicket, friendTicket);
            if (difference <= K)
            {
                count++;
            }
        }

        return count;
    }

    private static int GetBitDifference(int num1, int num2)
    {
        int xorResult = num1 ^ num2;
        int count = 0;
        while (xorResult > 0)
        {
            if ((xorResult & 1) == 1)
            {
                count++;
            }
            xorResult >>= 1;
        }
        return count;
    }

    static void Main(string[] args)
    {
        int N = 4;
        int M = 3;
        int K = 1;
        int[] tickets = { 5, 2, 3, 1, 4 };
        Console.WriteLine(CountFriendsWithSimilarTickets(N, M, K, tickets)); // Output should be 2
    }
}
```

