using System.Net.Http.Headers;
using System.Threading.Tasks;

public class RestServiceWrapper
{
    private HttpClient httpClient;
    private string authToken;

    public RestServiceWrapper(string baseUrl, string authToken)
    {
        httpClient = new HttpClient();
        httpClient.BaseAddress = new Uri(baseUrl);
        this.authToken = authToken;
        httpClient.DefaultRequestHeaders.Authorization = new AuthenticationHeaderValue("Bearer", this.authToken);
    }

    public async Task<string> Get(string endpoint)
    {
        HttpResponseMessage response = await httpClient.GetAsync(endpoint);
        response.EnsureSuccessStatusCode();
        return await response.Content.ReadAsStringAsync();
    }

    public async Task<string> Post(string endpoint, string content)
    {
        HttpContent httpContent = new StringContent(content);
        httpContent.Headers.ContentType = new MediaTypeHeaderValue("application/json");

        HttpResponseMessage response = await httpClient.PostAsync(endpoint, httpContent);
        response.EnsureSuccessStatusCode();
        return await response.Content.ReadAsStringAsync();
    }

    public async Task<string> Put(string endpoint, string content)
    {
        HttpContent httpContent = new StringContent(content);
        httpContent.Headers.ContentType = new MediaTypeHeaderValue("application/json");

        HttpResponseMessage response = await httpClient.PutAsync(endpoint, httpContent);
        response.EnsureSuccessStatusCode();
        return await response.Content.ReadAsStringAsync();
    }

    public async Task<string> Delete(string endpoint)
    {
        HttpResponseMessage response = await httpClient.DeleteAsync(endpoint);
        response.EnsureSuccessStatusCode();
        return await response.Content.ReadAsStringAsync();
    }
}
